        [Fact]
        public async Task GetByIDShouldReturnValidTitle()
        {
            //ARRANGE
            var handlerMock = new Mock<HttpMessageHandler>(MockBehavior.Strict);
            handlerMock
            .Protected()
            // Setup the PROTECTED method to mock
            .Setup<Task<HttpResponseMessage>>(
                "SendAsync",
                ItExpr.IsAny<HttpRequestMessage>(),
                ItExpr.IsAny<CancellationToken>()
            )
            // prepare the expected response of the mocked http call
            .ReturnsAsync(new HttpResponseMessage()
            {
                StatusCode = HttpStatusCode.OK,
                Content = new StringContent(jsonString),
            })
            .Verifiable();

            // use real http client with mocked handler here
            var httpClient = new HttpClient(handlerMock.Object)
            {
                BaseAddress = new Uri("http://test.com/"),
            };

            SerializerFactory serializerFactory = new SerializerFactory();

            MashupRepository mashupRepository = new MashupRepository(httpClient, serializerFactory);



            Musicbrainz artist =  await mashupRepository.Musicbrainz.GetArtist("5b11f4ce-a62d-471e-81fc-a69a8278c7da");
            Assert.Equal("Q11649", artist.WikidataId);

            
        }
